mapper.select:

		SELECT * FROM
		(SELECT COUNT(*)as B_COUNT, PRODUCT_SIZE , TO_CHAR (BID_PRICE,'999,999,999,999') as BID_PRICE
		FROM
		BID
		WHERE PRODUCT_ID = #{pid}
		AND BID_TYPE = #{type}
		AND PRODUCT_SIZE = #{size}
		GROUP BY PRODUCT_SIZE,BID_PRICE
		ORDER BY BID_PRICE DESC
		ORDER BY BID_PRICE ASC
		)
 		 WHERE ROWNUM < 6

SELECT TO_CHAR(MAX(BID_PRICE),'999,999,999,999') AS SELL_PRICE FROM BID
WHERE PRODUCT_ID = #{pid}
AND BID_TYPE='BUY'
AND PRODUCT_SIZE = #{size}

SELECT TO_CHAR(MIN(BID_PRICE),'999,999,999,999') AS BUY_PRICE FROM BID
WHERE PRODUCT_ID = #{pid}
AND BID_TYPE='SELL'
AND PRODUCT_SIZE = #{size}

mapper.update:
UPDATE BID


	BID_NO = #{bidNo},
	PRODUCT_ID = #{productId},
	BID_PRICE = #{bidPrice},
	BID_TYPE = #{BIDTYPE},
	CLIENT_ID = #{clientId}
	PRODUCT_SIZE = #{productSize}

WHERE BID_NO = #{bidNo}
