mapper.select:

		SELECT * FROM
		(SELECT COUNT(*)as B_COUNT, PRODUCT_SIZE , BID_PRICE
		FROM BID
		WHERE PRODUCT_ID = #{pid}
		AND BID_TYPE = #{type}
		GROUP BY PRODUCT_SIZE,BID_PRICE
		AND PRODUCT_SIZE = #{size}
		ORDER BY BID_PRICE DESC
		ORDER BY BID_PRICE ASC
		)
 		 WHERE ROWNUM < 6

mapper.update:
UPDATE BID


	BID_NO = #{bidNo},
	PRODUCT_ID = #{productId},
	BID_PRICE = #{bidPrice},
	BID_TYPE = #{BIDTYPE},
	CLIENT_ID = #{clientId}
	PRODUCT_SIZE = #{productSize}

WHERE BID_NO = #{bidNo}
