mapper.select:
SELECT TO_CHAR(D.DEAL_DATE, 'YYYY-MM-DD') AS DEAL_DATE ,D.DEAL_NO,D.DEAL_STATE,D.DEAL_PRICE,D.DEAL_BUYER,D.DEAL_SELLER, D.PRODUCT_ID,D.PRODUCT_SIZE ,P.PRODUCT_NAME, P.PRODUCT_MAKER, I.PRODUCTIMG_PATH, I.PRODUCTIMG_NAME1,C.CLIENT_NAME,C.CLIENT_ADDRESS,C.CLIENT_TEL,M.BANK_ACCOUNT ,M.BANK_NAME, M.CARD_NUM,M.CARD_CO FROM PRODUCTIMG I RIGHT OUTER JOIN PRODUCT P ON P.PRODUCT_ID = I.PRODUCT_ID RIGHT OUTER JOIN DEAL D ON D.PRODUCT_ID = P.PRODUCT_ID RIGHT OUTER JOIN CLIENT C ON D.DEAL_BUYER = C.CLIENT_ID
RIGHT OUTER JOIN CLIENT C ON D.DEAL_SELLER = C.CLIENT_ID
RIGHT OUTER JOIN PAYMENT M ON M.CLIENT_ID = C.CLIENT_ID WHERE D.DEAL_NO= #{dealNo} AND D.DEAL_BUYER = #{dealBuyer} AND D.DEAL_SELLER = #{dealSeller}

mapper.update:
UPDATE DEAL
DEAL_NO = #{dealNo}, DEAL_DATE = #{dealDate}, DEAL_STATE = #{dealState}, DEAL_PRICE = #{dealPrice}, DEAL_BUYER = #{dealBuyer}, DEAL_SELLER = #{dealSeller}, PRODUCT_ID = #{productId}, PRODUCT_SIZE = #{productSize}, DEAL_COMENT = #{dealComent}
WHERE DEAL_NO = #{dealNo}
